'use strict';

window.addEventListener('DOMContentLoaded', function () {
  document.querySelector('.nav__list').classList.add('hidden');
});
var navBtn = document.querySelector('.nav__btn');
var navMenu = document.querySelector('.nav__list');
var body = document.querySelector('.body');
var navItem = document.querySelectorAll('.nav__item');

function noScroll(event) {
  event.preventDefault();
}

navBtn.addEventListener('click', function () {
  navMenu.classList.toggle('hidden');
  navBtn.classList.toggle('close');

  if (!navMenu.classList.contains('hidden')) {
    body.addEventListener('touchmove', noScroll, {
      passive: false
    });
    body.addEventListener('mousewheel', noScroll, {
      passive: false
    });
  } else {
    body.removeEventListener('touchmove', noScroll, {
      passive: false
    });
    body.removeEventListener('mousewheel', noScroll, {
      passive: false
    });
  }
});

for (var i = 0; i < navItem.length; i++) {
  navItem[i].addEventListener('click', function () {
    navMenu.classList.remove('hidden');
  });
}
